import React, { useEffect, useState, useCallback } from 'react';
import { useChat } from '../../hooks/useChat';
import { useAuth } from '../../hooks/useAuth';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import ChatSidebar from './ChatSidebar';
import Loading from '../../common/Loading';
import ErrorMessage from '../../common/ErrorMessage';
import { Menu, Settings, MoreVertical } from 'lucide-react';

const ChatRoom = () => {
  const { user } = useAuth();
  const {
    currentChatRoom,
    messages,
    loading,
    error,
    sendMessage,
    loadChatRooms,
    clearError,
    createChatRoom,
    selectChatRoom
  } = useChat();
  
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [settingsOpen, setSettingsOpen] = useState(false);
  const [hasLoadedChatRooms, setHasLoadedChatRooms] = useState(false);

  // useEffectÎ•º ÏïàÏ†ÑÌïòÍ≤å Ï≤òÎ¶¨ÌïòÍ∏∞ ÏúÑÌï¥ Î≥ÑÎèÑÏùò ÏÉÅÌÉúÎ°ú Í¥ÄÎ¶¨
  useEffect(() => {
    if (user && user.id && !hasLoadedChatRooms) {
      loadChatRooms(user.id);
      setHasLoadedChatRooms(true);
    }
  }, [user?.id, hasLoadedChatRooms, loadChatRooms]);

  const handleSendMessage = useCallback(async (content) => {
    try {
      await sendMessage(content);
    } catch (error) {
      console.error('Failed to send message:', error);
    }
  }, [sendMessage]);

  const handleToggleSidebar = useCallback(() => {
    setSidebarOpen(prev => !prev);
  }, []);

  const handleToggleSettings = useCallback(() => {
    setSettingsOpen(prev => !prev);
  }, []);

  const handleCloseOverlay = useCallback(() => {
    setSidebarOpen(false);
    setSettingsOpen(false);
  }, []);

  const handleRetry = useCallback(() => {
    if (user?.id) {
      setHasLoadedChatRooms(false); // Ïû¨Î°úÎìúÎ•º ÏúÑÌï¥ ÏÉÅÌÉú Î¶¨ÏÖã
    }
  }, [user?.id]);

  // Îπ†Î•∏ ÏãúÏûë Í∏∞Îä•
  const handleQuickStart = useCallback(async () => {
    try {
      const newRoom = await createChatRoom(user.id, 'AIÏôÄÏùò Ï≤´ ÎåÄÌôî');
      await selectChatRoom(newRoom);
    } catch (error) {
      console.error('Failed to quick start:', error);
    }
  }, [user?.id, createChatRoom, selectChatRoom]);

  return (
    <div className="chat-room">
      {/* ÏÇ¨Ïù¥ÎìúÎ∞î */}
      <ChatSidebar 
        isOpen={sidebarOpen}
        onClose={() => setSidebarOpen(false)}
      />

      {/* Î©îÏù∏ Ï±ÑÌåÖ ÏòÅÏó≠ */}
      <div className={`chat-main ${sidebarOpen ? 'sidebar-open' : ''}`}>
        {/* Ï±ÑÌåÖ Ìó§Îçî */}
        <div className="chat-header">
          <div className="chat-header-left">
            <button 
              className="sidebar-toggle"
              onClick={handleToggleSidebar}
              title="Ï±ÑÌåÖ Î™©Î°ù"
            >
              <Menu size={20} />
            </button>
            <div className="chat-title">
              <h3>{currentChatRoom?.title || 'SeoulTech Chat - ÌÖåÏä§Ìä∏ Î™®Îìú'}</h3>
              {currentChatRoom && (
                <span className="chat-subtitle">
                  AIÏôÄ ÎåÄÌôî Ï§ë ‚Ä¢ {messages.length}Í∞ú Î©îÏãúÏßÄ
                </span>
              )}
            </div>
          </div>
          
          <div className="chat-header-right">
            <button 
              className="chat-settings"
              onClick={handleToggleSettings}
              title="ÏÑ§Ï†ï"
            >
              <MoreVertical size={20} />
            </button>
            
            {settingsOpen && (
              <div className="settings-dropdown">
                <button className="dropdown-item">
                  <Settings size={16} />
                  Ï±ÑÌåÖ ÏÑ§Ï†ï
                </button>
                <button className="dropdown-item">
                  ÎåÄÌôî ÎÇ¥Î≥¥ÎÇ¥Í∏∞
                </button>
                <button className="dropdown-item danger">
                  Ï±ÑÌåÖÎ∞© ÏÇ≠Ï†ú
                </button>
              </div>
            )}
          </div>
        </div>

        {/* ÏóêÎü¨ Î©îÏãúÏßÄ */}
        {error && (
          <ErrorMessage 
            error={error} 
            onClose={clearError}
            onRetry={handleRetry}
          />
        )}

        {/* Ï±ÑÌåÖ ÎÇ¥Ïö© */}
        <div className="chat-content">
          {currentChatRoom ? (
            <>
              <MessageList 
                messages={messages} 
                loading={loading}
                currentUser={user}
              />
              <MessageInput 
                onSend={handleSendMessage}
                disabled={loading}
                placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
              />
            </>
          ) : (
            <div className="chat-welcome">
              <div className="welcome-content">
                <div className="welcome-icon">ü§ñ</div>
                <h2>ÏÑúÏö∏Í≥ºÌïôÍ∏∞Ïà†ÎåÄÌïôÍµê AI Ï±óÎ¥á</h2>
                <p>ÏïàÎÖïÌïòÏÑ∏Ïöî! ÌÖåÏä§Ìä∏ Î™®ÎìúÎ°ú Ïã§Ìñâ Ï§ëÏûÖÎãàÎã§.</p>
                <p>ÏÉàÎ°úÏö¥ Ï±ÑÌåÖÏùÑ ÏãúÏûëÌïòÏó¨ AIÏôÄ ÎåÄÌôîÌï¥Î≥¥ÏÑ∏Ïöî.</p>
                
                <div style={{ marginTop: '2rem', padding: '1rem', backgroundColor: 'var(--bg-secondary)', borderRadius: '8px' }}>
                  <p><strong>üí° Ï∂îÏ≤ú ÏßàÎ¨∏Îì§:</strong></p>
                  <ul style={{ textAlign: 'left', marginTop: '0.5rem' }}>
                    <li>"ÏïàÎÖïÌïòÏÑ∏Ïöî"</li>
                    <li>"ÏÑúÏö∏Í≥ºÍ∏∞ÎåÄÏóê ÎåÄÌï¥ ÏïåÎ†§Ï£ºÏÑ∏Ïöî"</li>
                    <li>"Ï†ÑÍ≥µ Ï†ïÎ≥¥Í∞Ä Í∂ÅÍ∏àÌï¥Ïöî"</li>
                    <li>"Ï∑®ÏóÖÎ•†ÏùÄ Ïñ¥Îñ§Í∞ÄÏöî?"</li>
                    <li>"Ï∫†ÌçºÏä§ ÏÉùÌôúÏùÄ Ïñ¥Îñ§Í∞ÄÏöî?"</li>
                  </ul>
                </div>
                
                <div style={{ marginTop: '2rem', display: 'flex', gap: '1rem', justifyContent: 'center' }}>
                  <button 
                    className="btn btn-primary"
                    onClick={handleQuickStart}
                    style={{ padding: '0.75rem 1.5rem' }}
                  >
                    Î∞îÎ°ú ÏãúÏûëÌïòÍ∏∞
                  </button>
                  <button 
                    className="btn btn-outline"
                    onClick={handleToggleSidebar}
                    style={{ padding: '0.75rem 1.5rem' }}
                  >
                    Ï±ÑÌåÖ Î™©Î°ù Î≥¥Í∏∞
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Ïò§Î≤ÑÎ†àÏù¥ */}
      {(sidebarOpen || settingsOpen) && (
        <div 
          className="overlay"
          onClick={handleCloseOverlay}
        />
      )}
    </div>
  );
};

export default ChatRoom;
