import React, { useEffect, useState, useCallback, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useChat } from '../../hooks/useChat';
import { useAuth } from '../../hooks/useAuth';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import ChatSidebar from './ChatSidebar';
import Loading from '../../common/Loading';
import ErrorMessage from '../../common/ErrorMessage';
import { Menu, MoreVertical, LogOut, Download, Trash2 } from 'lucide-react';

const ChatRoom = () => {
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const {
    currentChatRoom,
    messages,
    loading,
    error,
    sendMessage,
    loadChatRooms,
    clearError,
    createChatRoom,
    selectChatRoom,
    deleteChatRoom
  } = useChat();
  
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [settingsOpen, setSettingsOpen] = useState(false);
  const [hasLoadedChatRooms, setHasLoadedChatRooms] = useState(false);
  const dropdownRef = useRef(null);
  const buttonRef = useRef(null);

  // useEffectÎ•º ÏïàÏ†ÑÌïòÍ≤å Ï≤òÎ¶¨ÌïòÍ∏∞ ÏúÑÌï¥ Î≥ÑÎèÑÏùò ÏÉÅÌÉúÎ°ú Í¥ÄÎ¶¨
  useEffect(() => {
    if (user && user.id && !hasLoadedChatRooms) {
      loadChatRooms(user.id);
      setHasLoadedChatRooms(true);
    }
  }, [user?.id, hasLoadedChatRooms, loadChatRooms]);

  // Ïô∏Î∂Ä ÌÅ¥Î¶≠ Í∞êÏßÄ
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target) &&
          buttonRef.current && !buttonRef.current.contains(event.target)) {
        setSettingsOpen(false);
      }
    };

    if (settingsOpen) {
      document.addEventListener('click', handleClickOutside);
      return () => {
        document.removeEventListener('click', handleClickOutside);
      };
    }
  }, [settingsOpen]);

  const handleSendMessage = useCallback(async (content) => {
    try {
      await sendMessage(content);
    } catch (error) {
      console.error('Failed to send message:', error);
    }
  }, [sendMessage]);

  const handleToggleSidebar = useCallback(() => {
    setSidebarOpen(prev => !prev);
  }, []);

  const handleToggleSettings = useCallback((e) => {
    e.stopPropagation();
    setSettingsOpen(prev => !prev);
  }, []);

  const handleCloseOverlay = useCallback(() => {
    setSidebarOpen(false);
  }, []);

  // ÎåÄÌôî ÎÇ¥Î≥¥ÎÇ¥Í∏∞ Ìï®Ïàò
  const handleExportChat = () => {
    console.log('ÎåÄÌôî ÎÇ¥Î≥¥ÎÇ¥Í∏∞ ÌÅ¥Î¶≠Îê®');
    
    if (!messages || messages.length === 0) {
      alert('ÎÇ¥Î≥¥ÎÇº ÎåÄÌôîÍ∞Ä ÏóÜÏäµÎãàÎã§.');
      return;
    }

    try {
      const chatContent = messages.map(msg => 
        `[${msg.role === 'user' ? 'ÏÇ¨Ïö©Ïûê' : 'AI'}] ${msg.content || msg.text || ''}`
      ).join('\n\n');

      const blob = new Blob([chatContent], { type: 'text/plain;charset=utf-8' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;
      a.download = `${currentChatRoom?.title || 'Ï±ÑÌåÖ'}_${new Date().toISOString().split('T')[0]}.txt`;
