import React, { useEffect } from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';
import { showSwaggerToken } from '../../utils/swaggerHelper';
import Layout from '../../common/Layout';
import ChatRoom from '../chat/ChatRoom';
import { ROUTES } from '../../utils/constants';

const ChatPage = () => {
  const { isAuthenticated, loading } = useAuth();

  useEffect(() => {
    // ÌéòÏù¥ÏßÄ Î°úÎìúÏãú ÌÜ†ÌÅ∞ ÌôïÏù∏
    const token = localStorage.getItem('token');
    console.log('ChatPage - Token:', token);
    console.log('ChatPage - isAuthenticated:', isAuthenticated);
    
    // SwaggerÏö© ÌÜ†ÌÅ∞ Ï∂úÎ†• (Í∞úÎ∞ú ÌôòÍ≤ΩÏóêÏÑúÎßå)
    if (token && process.env.NODE_ENV === 'development') {
      console.log('%cüîê Swagger Ïù∏Ï¶ù ÌÜ†ÌÅ∞Ïù¥ Ï§ÄÎπÑÎêòÏóàÏäµÎãàÎã§!', 'color: #2ecc71; font-size: 14px; font-weight: bold;');
      showSwaggerToken();
    }
  }, [isAuthenticated]);

  if (loading) {
    return (
      <Layout>
        <div className="page-loading">
          <div className="loading-spinner" />
          <p>Loading...</p>
        </div>
      </Layout>
    );
  }

  if (!isAuthenticated) {
    console.log('ChatPage - Not authenticated, redirecting to login');
    return <Navigate to={ROUTES.LOGIN} replace />;
  }

  return (
    <Layout showHeader={false} showFooter={false}>
      <div className="chat-page">
        <ChatRoom />
      </div>
    </Layout>
  );
};

export default ChatPage;
