import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useChat } from '../../hooks/useChat';
import { useAuth } from '../../hooks/useAuth';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import ChatSidebar from './ChatSidebar';
import Loading from '../../common/Loading';
import ErrorMessage from '../../common/ErrorMessage';
import { Menu, Settings, MoreVertical, LogOut, Download, Trash2 } from 'lucide-react';
import { ROUTES } from '../../utils/constants';

const ChatRoom = () => {
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const {
    currentChatRoom,
    messages,
    loading,
    error,
    sendMessage,
    loadChatRooms,
    clearError,
    createChatRoom,
    selectChatRoom,
    deleteChatRoom
  } = useChat();

  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [settingsOpen, setSettingsOpen] = useState(false);
  const [hasLoadedChatRooms, setHasLoadedChatRooms] = useState(false);

  // Ïô∏Î∂Ä ÌÅ¥Î¶≠ Í∞êÏßÄÎ•º ÏúÑÌïú useEffect Ï∂îÍ∞Ä
  useEffect(() => {
    const handleClickOutside = (event) => {
      const dropdown = document.querySelector('.settings-dropdown');
      const button = document.querySelector('.chat-settings');

      if (settingsOpen && dropdown && button && 
          !dropdown.contains(event.target) && 
          !button.contains(event.target)) {
        setSettingsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [settingsOpen]);

  const handleSendMessage = useCallback(async (content) => {
    try {
      await sendMessage(content);
    } catch (error) {
      console.error('Failed to send message:', error);
    }
  }, [sendMessage]);

  const handleToggleSidebar = useCallback(() => {
    setSidebarOpen(prev => !prev);
  }, []);

  const handleToggleSettings = useCallback(() => {
    setSettingsOpen(prev => !prev);
  }, []);

  const handleCloseOverlay = useCallback(() => {
    setSidebarOpen(false);
    setSettingsOpen(false);
  }, []);

  // Î°úÍ∑∏ÏïÑÏõÉ Ï≤òÎ¶¨
  const handleLogout = useCallback(() => {
    logout();
    navigate(ROUTES.HOME);
  }, [logout, navigate]);

  // Ï±ÑÌåÖÎ∞© ÏÇ≠Ï†ú Ï≤òÎ¶¨
  const handleDeleteChatRoom = useCallback(async () => {
    if (currentChatRoom && window.confirm('Ï†ïÎßêÎ°ú Ïù¥ Ï±ÑÌåÖÎ∞©ÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      try {
        await deleteChatRoom(currentChatRoom.id);
        setSettingsOpen(false);
      } catch (error) {
        console.error('Failed to delete chat room:', error);
      }
    }
  }, [currentChatRoom, deleteChatRoom]);

  // ÎåÄÌôî ÎÇ¥Î≥¥ÎÇ¥Í∏∞ Ï≤òÎ¶¨
  const handleExportChat = useCallback(() => {
    if (!messages || messages.length === 0) {
      alert('ÎÇ¥Î≥¥ÎÇº ÎåÄÌôîÍ∞Ä ÏóÜÏäµÎãàÎã§.');
      return;
    }

    const chatContent = messages.map(msg => 
      `[${msg.role === 'user' ? 'ÏÇ¨Ïö©Ïûê' : 'AI'}] ${msg.content || msg.text}`
    ).join('\n\n');

    const blob = new Blob([chatContent], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${currentChatRoom?.title || 'Ï±ÑÌåÖ'}_${new Date().toISOString().split('T')[0]}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
    setSettingsOpen(false);
  }, [messages, currentChatRoom]);

  const handleRetry = useCallback(() => {
    if (user?.id) {
      setHasLoadedChatRooms(false); // Ïû¨Î°úÎìúÎ•º ÏúÑÌï¥ ÏÉÅÌÉú Î¶¨ÏÖã
    }
  }, [user?.id]);

  // Îπ†Î•∏ ÏãúÏûë Í∏∞Îä•
  const handleQuickStart = useCallback(async () => {
    try {
      const newRoom = await createChatRoom(user.id, 'AIÏôÄÏùò Ï≤´ ÎåÄÌôî');
      await selectChatRoom(newRoom);
    } catch (error) {
      console.error('Failed to quick start:', error);
    }
  }, [user?.id, createChatRoom, selectChatRoom]);

  return (
    <div className="chat-room">
      {/* ÏÇ¨Ïù¥ÎìúÎ∞î */}
      <ChatSidebar 
        isOpen={sidebarOpen}
        onClose={() => setSidebarOpen(false)}
      />

      {/* Î©îÏù∏ Ï±ÑÌåÖ ÏòÅÏó≠ */}
      <div className={`chat-main ${sidebarOpen ? 'sidebar-open' : ''}`}>
        {/* Ï±ÑÌåÖ Ìó§Îçî */}
        <div className="chat-header">
          <div className="chat-header-left">
            <button 
              className="sidebar-toggle"
              onClick={handleToggleSidebar}
              title="Ï±ÑÌåÖ Î™©Î°ù"
            >
              <Menu size={20} />
            </button>
            <div className="chat-title">
              <h3>{currentChatRoom?.title || 'SeoulTech Chat - ÌÖåÏä§Ìä∏ Î™®Îìú'}</h3>
              {currentChatRoom && (
                <span className="chat-subtitle">
